mapper.select:
select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}

select setmeal.*, category.name as categoryName
from setmeal left outer join category on setmeal.category_id = category.id


        and setmeal.name like concat('%', #{name}, '%')


        and setmeal.category_id = #{categoryId}


        and setmeal.status = #{status}


order by setmeal.update_time desc

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
